//         }
//     }
// }



  render() {
    return (
        <div style={this.getStyle()}>
            <p> {this.props.todo.title} </p>
        </div>
    )
  }
}

//PropTypes
TodoItem.propTypes = {
    todos: PropTypes.object.isRequired
  }

//   const itemsStyle = {
//       backgroundColor: '#f3f3f3'
//   }

export default TodoItem;
